mapper.select:
SELECT


,yanxue.yanxue_name as yanxueName
,yanxue.yanxue_uuid_number as yanxueUuidNumber
,yanxue.yanxue_photo as yanxuePhoto
,yanxue.yanxue_fuzeren as yanxueFuzeren
,yanxue.yanxue_phone as yanxuePhone
,yanxue.yanxue_address as yanxueAddress
,yanxue.zan_number as zanNumber
,yanxue.cai_number as caiNumber
,yanxue.yanxue_types as yanxueTypes
,yanxue.yanxue_kucun_number as yanxueKucunNumber
,yanxue.yanxue_time as yanxueTime
,yanxue.yanxue_clicknum as yanxueClicknum
,yanxue.yanxue_content as yanxueContent
,yanxue.shangxia_types as shangxiaTypes
,yanxue.yanxue_delete as yanxueDelete
,yonghu.yonghu_name as yonghuName
,yonghu.yonghu_phone as yonghuPhone
,yonghu.yonghu_id_number as yonghuIdNumber
,yonghu.yonghu_photo as yonghuPhoto
,yonghu.yonghu_email as yonghuEmail
,yonghu.new_money as newMoney
,yonghu.jinyong_types as jinyongTypes

FROM yanxue_yuyue  a
left JOIN yanxue yanxue ON a.yanxue_id = yanxue.id
left JOIN yonghu yonghu ON a.yonghu_id = yonghu.id



        and a.id in

        #{item}



        and a.yanxue_yuyue_uuid_number like CONCAT('%',#{params.yanxueYuyueUuidNumber},'%')


        and (
            a.yanxue_id = #{params.yanxueId}
        )


        and (
            a.yonghu_id = #{params.yonghuId}
        )


        and a.yanxue_yuyue_text like CONCAT('%',#{params.yanxueYuyueText},'%')


          and UNIX_TIMESTAMP(a.insert_time) >= UNIX_TIMESTAMP(#{params.insertTimeStart}) 


          and UNIX_TIMESTAMP(a.insert_time) <= UNIX_TIMESTAMP(#{params.insertTimeEnd}) 


        and a.insert_time = #{params.insertTime}


        and a.yanxue_yuyue_yesno_types = #{params.yanxueYuyueYesnoTypes}


        and a.yanxue_yuyue_yesno_text like CONCAT('%',#{params.yanxueYuyueYesnoText},'%')


          and UNIX_TIMESTAMP(a.yanxue_yuyue_shenhe_time) >= UNIX_TIMESTAMP(#{params.yanxueYuyueShenheTimeStart}) 


          and UNIX_TIMESTAMP(a.yanxue_yuyue_shenhe_time) <= UNIX_TIMESTAMP(#{params.yanxueYuyueShenheTimeEnd}) 


        and a.yanxue_yuyue_shenhe_time = #{params.yanxueYuyueShenheTime}




        and a.yanxue_id IS NOT NULL


        and yanxue.yanxue_name like CONCAT('%',#{params.yanxueName},'%')


        and yanxue.yanxue_uuid_number like CONCAT('%',#{params.yanxueUuidNumber},'%')


        and yanxue.yanxue_fuzeren like CONCAT('%',#{params.yanxueFuzeren},'%')


        and yanxue.yanxue_phone like CONCAT('%',#{params.yanxuePhone},'%')


        and yanxue.yanxue_address like CONCAT('%',#{params.yanxueAddress},'%')


          and yanxue.zan_number >= #{params.zanNumberStart}   


          and yanxue.zan_number <= #{params.zanNumberEnd}   


        and yanxue.zan_number = #{params.zanNumber}


          and yanxue.cai_number >= #{params.caiNumberStart}   


          and yanxue.cai_number <= #{params.caiNumberEnd}   


        and yanxue.cai_number = #{params.caiNumber}


        and yanxue.yanxue_types = #{params.yanxueTypes}



          and yanxue.yanxue_kucun_number >= #{params.yanxueKucunNumberStart}   


          and yanxue.yanxue_kucun_number <= #{params.yanxueKucunNumberEnd}   


        and yanxue.yanxue_kucun_number = #{params.yanxueKucunNumber}


          and UNIX_TIMESTAMP(yanxue.yanxue_time) >= UNIX_TIMESTAMP(#{params.yanxueTimeStart}) 


          and UNIX_TIMESTAMP(yanxue.yanxue_time) <= UNIX_TIMESTAMP(#{params.yanxueTimeEnd}) 


        and a.yanxue_time = #{params.yanxueTime}


          and yanxue.yanxue_clicknum >= #{params.yanxueClicknumStart}   


          and yanxue.yanxue_clicknum <= #{params.yanxueClicknumEnd}   


        and yanxue.yanxue_clicknum = #{params.yanxueClicknum}


        and yanxue.yanxue_content like CONCAT('%',#{params.yanxueContent},'%')


        and yanxue.shangxia_types = #{params.shangxiaTypes}



          and yanxue.yanxue_delete >= #{params.yanxueDeleteStart}   


          and yanxue.yanxue_delete <= #{params.yanxueDeleteEnd}   


        and yanxue.yanxue_delete = #{params.yanxueDelete}


          and UNIX_TIMESTAMP(yanxue.insert_time) >= UNIX_TIMESTAMP(#{params.insertTimeStart}) 


          and UNIX_TIMESTAMP(yanxue.insert_time) <= UNIX_TIMESTAMP(#{params.insertTimeEnd}) 


        and a.insert_time = #{params.insertTime}



        and a.yonghu_id IS NOT NULL


        and yonghu.yonghu_name like CONCAT('%',#{params.yonghuName},'%')


        and yonghu.yonghu_phone like CONCAT('%',#{params.yonghuPhone},'%')


        and yonghu.yonghu_id_number like CONCAT('%',#{params.yonghuIdNumber},'%')


        and yonghu.yonghu_email like CONCAT('%',#{params.yonghuEmail},'%')


          and yonghu.new_money >= #{params.newMoneyStart}   


          and yonghu.new_money <= #{params.newMoneyEnd}   


        and a.new_money = #{params.newMoney}


        and yonghu.jinyong_types = #{params.jinyongTypes}



order by a.${params.sort} ${params.order}
